package com.mht2html2txt.allstar.annotation;import java.lang.annotation.ElementType;import java.lang.annotation.Retention;import java.lang.annotation.RetentionPolicy;import java.lang.annotation.Target;/** * 自己手写的注解 *  * @author admin * */@Target(ElementType.METHOD)@Retention(RetentionPolicy.RUNTIME)public @interface MineAnnotation {	/**	 * 记录日志的操作类型，不写默认值就是一个必须填的注解	 * 	 * @return	 */	String operateDescription();}