package com.mht2html2txt.allstar.listener;import javax.servlet.annotation.WebListener;import javax.servlet.http.HttpSessionEvent;import javax.servlet.http.HttpSessionListener;import lombok.extern.slf4j.Slf4j;/** * 监听器类 *  * @author admin * */@WebListener()@Slf4jpublic class MineListener implements HttpSessionListener {	public static int online = 0;	@Override	public void sessionCreated(HttpSessionEvent se) {		log.info("\n创建session,在线用户数:" + (++online));	}	@Override	public void sessionDestroyed(HttpSessionEvent se) {		log.info("\n销毁session,在线用户数:" + (--online));		online--;	}}